sql.if:
WITH direct_with_user AS MATERIALIZED (
    SELECT ns.*
    FROM namespaces AS ns INNER JOIN members AS mb ON ns.id = mb.namespace_id
    WHERE mb.user_id = #{filter.userId,jdbcType=INTEGER}

        AND ns.traversal_ids[#{filter.parentLevel,jdbcType=INTEGER}] = #{filter.parentId,jdbcType=INTEGER}

)

WITH direct_with_viewer AS MATERIALIZED (
    SELECT ns.*
    FROM namespaces AS ns INNER JOIN members AS mb ON ns.id = mb.namespace_id
    WHERE mb.user_id = #{filter.viewerId,jdbcType=INTEGER}

        AND ns.traversal_ids[#{filter.parentLevel,jdbcType=INTEGER}] = #{filter.parentId,jdbcType=INTEGER}

)

mapper.sql:
SELECT ns.* FROM namespaces AS ns INNER JOIN (
SELECT DISTINCT UNNEST(du.traversal_ids[#{filter.parentLevel,jdbcType=INTEGER} + 1:]) FROM direct_with_user AS du WHERE du.traversal_ids[#{filter.parentLevel,jdbcType=INTEGER}] = #{filter.parentId,jdbcType=INTEGER}
SELECT DISTINCT du.traversal_ids[#{filter.parentLevel,jdbcType=INTEGER} + 1] FROM direct_with_user AS du WHERE du.traversal_ids[#{filter.parentLevel,jdbcType=INTEGER}] = #{filter.parentId,jdbcType=INTEGER}
SELECT DISTINCT UNNEST(du.traversal_ids) FROM direct_with_user AS du
SELECT DISTINCT du.traversal_ids[1] FROM direct_with_user AS du
) AS ancestors (ancestor_id) ON ns.id = ancestors.ancestor_id

choose.when:
AND (
    ns.created_at = #{cursor.createdAt,jdbcType=TIMESTAMP_WITH_TIMEZONE} AND ns.id > #{cursor.id,jdbcType=INTEGER}
    OR ns.created_at  #{cursor.createdAt,jdbcType=TIMESTAMP_WITH_TIMEZONE}
)

AND (
    ns.updated_at = #{cursor.updatedAt,jdbcType=TIMESTAMP_WITH_TIMEZONE} AND ns.id > #{cursor.id,jdbcType=INTEGER}
    OR ns.updated_at  #{cursor.updatedAt,jdbcType=TIMESTAMP_WITH_TIMEZONE}
)

AND (
    ns.path = #{cursor.path,jdbcType=VARCHAR} AND ns.id > #{cursor.id,jdbcType=INTEGER}
    OR ns.path  #{cursor.path,jdbcType=VARCHAR}
)

